<script>
    var page=1;let e=document.getElementById("scroll-content");var loadMore=document.getElementById("loadMore");loadMore.addEventListener("click",function(){fetch("{{ route('categoryArticles', $category->slug) }}",{method:"POST",headers:{"Content-Type":"application/json","X-Requested-With":"XMLHttpRequest","X-CSRF-Token":document.querySelector('meta[name="csrf-token"]').content},body:JSON.stringify({page})}).then(t=>t.json()).then(t=>{if(t&&t.length>0){var a="";for(let l=0;l<t.length;l++)a+='<div class="col-md-6 col-lg-4 mb-1 single-post">',a+='<div class="category-post">',a+='<div class="image">',a+='<figure class="m-0">',a+='<a href="'+t[l].url+'">',a+='<img src="'+t[l].image+'" alt="" class="image_img img-fluid">',a+="</a>",a+="</figure>",a+="</div>",a+='<div class="category-post-title">',a+='<div class="title mb-3">',a+='<a href="'+t[l].url+'">',a+=t[l].title,a+="</a>",a+="</div>",a+='<div class="meta"><p class = "article-date" >'+t[l].published_at+" | </p>",a+='<p class="article-author">',a+='<a href="'+t[l].author.url+'"> '+t[l].author.alias_name+"</a></p>",a+="</div>",a+="</div>",a+="</div>",a+="</div>";e.insertAdjacentHTML("beforeend",a),page++}})});
</script>
